import web3 from './ethereum/web3'

import React from 'react';
import DisputeDetail from './DisputeDetail'

class Dispute extends React.Component {

constructor(props){
  super(props)
  console.log(props)
}

disputeStatusToString = (code) => {
  switch (code) {
    case "0":
      return "Waiting"
      break;
    case "1":
      return "Appealable"
      break;
    case "2":
      return "Solved"
      break;

    default:
      return ""+code
  }

}

  render(){
    return (
          <React.Fragment>
            <tbody>
              <tr className="card" type="button" data-toggle="collapse" data-target={'#accordion + this.props.id'}  aria-expanded="false" aria-controls={'accordion + this.props.id'}>
                  <td>{this.props.id}</td>
                  <td>{this.props.arbitrated.substring(0, 12) + '...'}</td>
                  <td>{web3.utils.fromWei(this.props.fee, 'ether')}</td>
                  <td>{this.props.ruling}</td>
                  <td>{this.disputeStatusToString(this.props.status)}</td>
              </tr>
            </tbody>
            <tbody>
              <tr>
                  <td colSpan="5">
                      <div id={'accordion + this.props.id'} className="collapse.show" data-toggle="collapse">Hidden by default</div>
                  </td>
              </tr>
            </tbody>
          </React.Fragment>
    )
  }
}

export default Dispute
